    /* important - we need the original SVG's width and height */
    var svgWidth = 959;
    var svgHeight = 593;
    var c = Raphael('us-map', "100%", "100%");
    c.safari();
    var label = c.popup(50, 50, "").hide();
    attr = {
        fill: '#FAD6A5',
        stroke: '#000',
        'stroke-width': 1,
        'stroke-linejoin': 'round'
    }
    /* the following line makes the Raphael paper fill its container (us-map) */
    c.setViewBox(0, 0, svgWidth, svgHeight, true);

    var glow = false;
    var ie = false;
    var ie_old = false;
    var safari = false;
    if (navigator.userAgent) {
        if (navigator.userAgent.indexOf("iPad") != -1 || navigator.userAgent.indexOf("iPhone") != -1) {
            // careful, chrome browser has safari in agent, so also check for Chrome string
            safari = true;
        }
    }
    if (navigator.appName.indexOf("Internet Explorer") != -1) {
        ie = true;
        if (navigator.userAgent.indexOf("compatible") == -1 && (navigator.appVersion.indexOf("MSIE 6") != -1 || navigator.appVersion.indexOf("MSIE 7") != -1)) {
            // an IE browser that is IE6 or IE7 natively..  not in compatibility mode
            ie_old = true;
            alert('We\'re sorry.  Your browser does not support the features on this map.  Please consider upgrading to a newer version or access the text-only version of this page.');
            return;
        }
        if (navigator.userAgent.indexOf("compatible") != -1 && navigator.appVersion.indexOf("MSIE 7") != -1) {
            // an IE browser that is in IE7 compatibility mode.  probably an IE8 browser?
            alert('Your browser appears to be in compatibilty mode for IE7 and may not work well with this interactive map.  Please consider upgrading to a newer version.');
            ie_old = true;
        } else if (navigator.appVersion.indexOf("MSIE 8") != -1) {
            // an IE browser that is IE8 .. whether in compatibility mode or not
            alert('Your browser is outdated and may not work well with this interactive map.  Please consider upgrading to a newer version.');
            ie_old = true;
        }

    }
    var lastone = false;
    var lastlastone = false;

    function hoverover() {
        
        // make the cursor a hand
        this.attr({
            cursor: "pointer"
        });
        if (!glow) {
            if (!ie) {
                this.toFront();
            }

            label.toFront();
            glow = true;
            
        };

        if (ie) {
            ieHover(this);
        } else {
            this.animate({
                transform: 's1.3t' + this.adjustx + ',' + this.adjusty,
                fill: "#c60"
            }, 200);
        }
        abbrev = this.abbrev.toLowerCase();
        if (abbrev == 'va' || abbrev == 'md') {
            // always place DC star under the map after hovering ON Maryland or Virginia
            setTimeout(function() {
                dc.toBack()
            }, 2);
        }

        bbox = this.getBBox();
        label.attr({
            text: paths[abbrev].name
        }).update(bbox.x + 8 + paths[abbrev].offsetx, bbox.y + bbox.height / 2 + paths[abbrev].offsety, bbox.width - 50).toFront().show();
        //lines.toFront();
    }

    function ieHover(region) {
        if (region.attr('fill') != "#c60") {
            // clean up previous hovers..  ie bug
            if (lastlastone) {
                if (!ie_old) {
                    lastlastone.transform('s1');
                }
                lastlastone.attr('fill', '#FAD6A5');
                lastlastone.toBack();
            }
            if (lastone) {
                if (!ie_old) {
                    lastone.transform('s1');
                }
                lastone.attr('fill', '#FAD6A5');
                lastone.toBack();
            }

            region.attr("fill", "#c60");
            var obj = region;
            // attempt to show some basic animation for ie>=8 without using raphael 'animate' method
            if (ie_old) {
                // don't do any scaling at all with IE8
                //obj.transform('s1.2t' + obj.adjustx*.85  + "," + obj.adjusty*.85);
            } else {
                setTimeout(function() {
                    obj.transform('s1.05t' + obj.adjustx * .25 + "," + obj.adjusty * .25);
                }, 12);
                setTimeout(function() {
                    obj.transform('s1.1t' + obj.adjustx * .5 + "," + obj.adjusty * .5);
                }, 24);
                setTimeout(function() {
                    obj.transform('s1.15t' + obj.adjustx * .75 + "," + obj.adjusty * .75);
                }, 36);

            }
            region.toFront();
            lastlastone = lastone;
            lastone = region;
            if (region.abbrev != 'VA' && region.abbrev != 'MD') {
                dc.toFront();
                lines.toFront();
            }
        }
    }

    function hoveroff() {
        // what to do when we hover off
        glow = false;
        if (ie) {
            if (!ie_old) {
                this.transform('s1');
            }
            this.attr('fill', attr.fill);
            this.toBack();
        } else {
            // must animate for longer than the hoveron time
            this.animate({
                transform: '0,0,0,s1',
                fill: attr.fill
            }, 201, "bounce");
            if (this.abbrev == 'VA' || this.abbrev == 'MD') {
                // always place DC star on top of map after hovering off Maryland or Virginia
                setTimeout(function() {
                    dc.toFront()
                }, 1);
            }

        }

        label.hide();
        labels.toFront();
        for (var item in blackbox) {
            whitelabel[item].toFront();
            blackbox[item].toFront();
        }
        // make sure the DC star is still on top of the map.  probably more of an issue with IE
        dc.toFront();

        // make sure the lines are on top of the map (these are the lines from outsidelabels pointing to small state regions)
        lines.toFront();
    }

    function clicked() {
        // what to do when you click on a state
        var stateName;

        // first check if the click came from a label that was outside the US boundary
        var abbrev = labelid2abbrev[this.id];

        if (typeof abbrev === 'undefined') {
            // must have clicked on a state region since the id is not one of the outside labels
            abbrev = abbrev_array[this.id];
            stateName = paths[abbrev].name;
        } else {
            // clicked on a label outside the US (eg. MA, RI, CT..)
            stateName = paths[abbrev].name;
        }
        //JLS WaterFall integration change 
        openStateDialog(abbrev, stateName);
    }

    function hoveroverabbrev(e) {
        //alert(this.id);
        var abbrev = labelid2abbrev[this.id];
        // set p to the raphael object for the state based on the state's abbrev
        p = parray[abbrev];
        
        // set cursor to a hand instead of a text cursor 
        this.attr({
            cursor: "pointer"
        });
        if (!glow) {
            if (!ie) {
                p.toFront();
            }

            label.toFront();
            glow = true;
        };
        // make the label bigger.  in this function 'this' object is the label for small states, not the state region itself.
        // 'p' object is the state region
        //this.transform('s1.2');  
        whitelabel[abbrev].transform('s1.2');

        // hide the label for this small state since it interferes with the hover in IE especially
        //blackbox[abbrev].toFront(); //.attr({'fill':'#000000'});
        //blackbox[abbrev].attr({'opacity':'.1'}); 

        if (ie) {
            // for IE, do some limited fake animation without using animate method
            ieHover(p);
        } else {
            // make the state region grow
            p.animate({
                transform: 's1.3',
                fill: "#c60"
            }, 200);
        }
        bbox = p.getBBox();
        label.attr({
            text: paths[abbrev].name
        }).update(bbox.x + 8 + paths[abbrev].offsetx, bbox.y + bbox.height / 2 + paths[abbrev].offsety, bbox.width - 50).toFront().show();
    }

    function hoveroffabbrev() {
        var abbrev = labelid2abbrev[this.id];
        p = parray[abbrev];
        glow = false;
        if (ie) {
            if (!ie_old) {
                p.transform('s1');
            }
            p.attr('fill', attr.fill);
            p.toBack();
        } else {
            p.animate({
                transform: 's1',
                fill: attr.fill
            }, 201, "bounce");
        }
        // make the (outside US) label normal size
        //this.transform('s1.0');
        whitelabel[abbrev].transform('s1.0');

        //hide the label that's inside the state region
        label.hide();
        labels.toFront();
        for (var item in blackbox) {
            whitelabel[item].toFront();
            blackbox[item].toFront();
        }
        //setTimeout(function(){dc.toFront()},1);
        dc.toFront();
        lines.toFront();
    }



